<?phpnamespace common\modules\translation;use soft\helpers\SiteHelper;use Yii;/** * Translation module definition class * @property-read array $gridColumns */class TranslationManager extends \yii\base\Module{    /**     * @var array Array of languages     */    public $languages;    public $grid_column = [];    /**     * @inheritdoc     */    public $controllerNamespace = 'common\modules\translation\controllers';    /**     * @inheritdoc     */    public function init()    {        parent::init();        $this->languages = array_keys(SiteHelper::languages());        $this->grid_column = $this->getGridColumns();    }    /**     * @return array Return array of column for gridViewWidget     */    public function getGridColumns()    {        $columns = [            'checkboxColumn' => [                'width' => '50px',            ],            [                'attribute' => 'message',                'headerOptions' => ['style' => 'max-width: 100px;'],                'contentOptions' => ['style' => 'max-width: 100px;vertical-align:middle;word-wrap:anywhere;white-space:break-spaces'],            ]        ];        foreach ($this->languages as $one) {            $value = $_GET['SourceMessageSearch']['languages'][$one] ?? '';            $columns[] = [                'label' => Yii::$app->params['languages'][$one],                'value' => 'languages.' . $one,                'format' => 'raw',                'headerOptions' => ['style' => 'max-width: 100px;'],                'contentOptions' => ['style' => 'max-width: 100px;vertical-align:middle;word-wrap:anywhere;white-space:break-spaces'],                'filter' => '<input type="text" value="' . $value . '" class="form-control" name="SourceMessageSearch[languages][' . $one . ']">',            ];        }        $columns[] = [            'class' => 'soft\grid\ActionColumn',            'width' => "150px",            'dropdown' => false,            'viewOptions' => [                'class' => 'btn btn-sm btn-outline-info',                'role' => 'modal-remote',                'label' => null,            ],            'updateOptions' => [                'class' => 'btn btn-sm btn-outline-success',                'role' => 'modal-remote',                'label' => null,            ],            'deleteOptions' => [                'class' => 'btn btn-sm btn-outline-danger',                'role' => 'modal-remote',                'label' => null,            ],        ];        return $columns;    }}