<?php// Translationuse soft\helpers\ArrayHelper;use soft\helpers\LatinCyrillConvertor;function t($title = "", $category = 'app', $params = [], $language = null){    return Yii::t($category, $title, $params, $language);}/** * @return false|int */function today(){    return strtotime('today');}function cyrill($text){    return LatinCyrillConvertor::latinToCyrill($text);}