<?phpnamespace soft\helpers;use Yii;use yii\helpers\Url as YiiUrl;class Url extends YiiUrl{    /**     * @param $params mixed     * @return string     */    public static function homeUrl($params = null)    {        return static::to('/', true);    }    public static function isHomePage()    {        return Yii::$app->controller->route == 'site/index';    }    public static function domain()    {        $baseUrl = self::base('');        return preg_replace("/[^A-Za-z0-9.-]/", '', $baseUrl);    }    /**     * @param $url mixed     * @param $addOnEmpty bool     * @return string     */    public static function withHostInfo($url, bool $addOnEmpty = false)    {        if ($url === null && !$addOnEmpty) {            return null;        }        $hostInfo = Yii::$app->request->hostInfo;        if (strpos($url, $hostInfo) === 0) {            return $url;        }        $url = Url::to($url);        return $hostInfo . $url;    }}?>