<?phpnamespace soft\web;use Yii;use yii\helpers\Url;use yii\web\UrlManager as YiiUrlManager;class UrlManager extends YiiUrlManager{    public $isMultilingual = true;    /**     * @param array $params     * @param bool $addLanguageParam     * @return string     */    public function createUrl($params=[], bool $addLanguageParam = true)    {        $params = (array) $params;        if ($this->isMultilingual && $addLanguageParam) {            $yiiParams = Yii::$app->params;            $languageParam = $yiiParams['languageParam'];            if (!isset($params[$languageParam])) {                $language = Yii::$app->request->get($languageParam, $yiiParams['defaultLanguage']);                if (!array_key_exists($language, $yiiParams['languages'])){                    $language = $yiiParams['defaultLanguage'];                }                $params[$languageParam] = $language;            }        }        return parent::createUrl($params);    }    /**     * {@inheritdoc}     * @param array|string $params     * @param null $scheme     * @param bool $addLanguageParam     * @return string     * @throws yii\base\InvalidConfigException     */    public function createAbsoluteUrl($params, $scheme = null, bool $addLanguageParam = false)    {        $url = $this->createUrl($params, $addLanguageParam);        if (strpos($url, '://') === false) {            $hostInfo = $this->getHostInfo();            if (strncmp($url, '//', 2) === 0) {                $url = substr($hostInfo, 0, strpos($hostInfo, '://')) . ':' . $url;            } else {                $url = $hostInfo . $url;            }        }        return Url::ensureScheme($url, $scheme);    }    /**     * @param $url mixed     * @return mixed|string     */    public function absoluteUrl($url)    {        if (strpos($url, '://') === false) {            $url = 'https://' . $url;        }        return $url;    }}