<?phpnamespace soft\widget\kartik;use odilov\multilingual\containers\MultilingualFieldContainer;use odilov\multilingual\widgets\FormLanguageSwitcher;class ActiveForm extends \kartik\widgets\ActiveForm{    public $fieldClass = 'soft\widget\kartik\ActiveField';    /**     * Renders form language switcher.     *     * @param \soft\db\ActiveRecord $model     * @param string $view     * @return string     * @throws \Throwable     */    public function languageSwitcher($model, $view = null)    {        if ($model->hasMultilingualBehavior) {            $languages = $model->getMultilingualBehavior()->languages;            return FormLanguageSwitcher::widget(['languages' => $languages, 'view' => $view]) . "<br>";        }        return '';    }    /**     * {@inheritdoc}     * @param \soft\db\ActiveRecord $model     * @return \soft\widget\kartik\ActiveField     */    public function field($model, $attribute, $options = [])    {        $fields = [];        $isNonMultilingual = (isset($options['multilingual']) && $options['multilingual'] === false);        $isFieldMultilingual = (isset($options['multilingual']) && $options['multilingual']);        $isAttributeMultilingual = ($model->getBehavior('multilingual') && $model->isMultilingualAttribute($attribute));        if (!$isNonMultilingual && ($isFieldMultilingual || $isAttributeMultilingual)) {            $languages = array_keys($model->getMultilingualBehavior()->languages);            foreach ($languages as $language) {                $fields[] = parent::field($model, $attribute, array_merge($options, ['language' => $language]));            }        } else {            return parent::field($model, $attribute, $options);        }        return new MultilingualFieldContainer(['fields' => $fields]);    }}